import React, { useState } from "react";
import "./App.css"; // You can style your app with CSS

function App() {
  // Define a state variable to store tasks
  const [tasks, setTasks] = useState([
    { text: "Learn JavaScript", completed: false },
    { text: "Learn React", completed: false },
    { text: "Build a React App", completed: false },
  ]);

  // Define a state variable for the input field
  const [taskText, setTaskText] = useState("");

  // Define a state variable for the search query
  const [searchQuery, setSearchQuery] = useState("");

  // Function to add a new task to the list
  const addTask = () => {
    if (taskText.trim() !== "") {
      setTasks([...tasks, { text: taskText, completed: false }]);
      setTaskText("");
    }
  };

  // Function to handle search input changes
  const handleSearchInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Function to toggle the completion status of a task
  const toggleTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);
  };

  // Function to delete a task from the list
  const deleteTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);
  };

  return (
    <div className="App">
      <h1>THINGS TO DO</h1>
      <div>
        <input
          type="text"
          placeholder="Add New"
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
        />
        <button onClick={addTask}>
          <FontAwesomeIcon icon={faPlus} /> Add
        </button>
      </div>
      <input
        type="text"
        placeholder="Search"
        value={searchQuery}
        onChange={handleSearchInputChange}
      />
      <ul>
        {tasks
          .filter((task) =>
            task.text.toLowerCase().includes(searchQuery.toLowerCase())
          )
          .map((task, index) => (
            <li key={index}>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTask(index)}
              />
              <span className={task.completed ? "completed" : ""}>
                {task.text}
              </span>
              <button onClick={() => deleteTask(index)}>Delete</button>
            </li>
          ))}
      </ul>
      {/* Footer */}
      <footer className="footer">
        <div className="footer-icons">
          <FontAwesomeIcon icon={faPlus} /> <FontAwesomeIcon icon={faSearch} />
        </div>
        <div className="item-count">{tasks.length} items</div>
        <div className="filters">
          <button onClick={() => setFilter("all")}>All</button>
          <button onClick={() => setFilter("active")}>Active</button>
          <button onClick={() => setFilter("completed")}>Completed</button>
        </div>
      </footer>
    </div>
  );
}

export default App;
